import React from 'react';
import CitySelect from '../city-select/CitySelect';
import TrainSelect from '../train-select/TrainSelect';
import LockSeat from '../lock-seat/LockSeat';

class TrainDetail extends React.Component {
  constructor (props) {
    super(props);
    this.state = {trainList: []};
    this.setTrainList = this.setTrainList.bind(this);
  }

  // CitySelect组件传回值的方法
  setTrainList = (trainList, date) => {
    this.setState({
      trainList: trainList,
      date: date,
    });
  };

  // CitySelect组件传回值的方法
  setTransferList = (transferList, date) => {
    this.setState({
      transferList: transferList,
      date: date,
    });
  };

  render () {
    return (
      <div className='train'>
        <CitySelect setTrainList={this.setTrainList} setTransferList={this.setTransferList}/>
        <TrainSelect trainList={this.state.trainList} date={this.state.date}/>
        <LockSeat/>
      </div>
    );
  }
}

export default TrainDetail;
