import React from 'react';
import './TrainSelect.css'
import Button from "../button/Button";

const TrainSelectTitle = () => {
  return (
    <div className='train-select-detail'>
      <div className='train-select-item'> 火车号</div>
      <div className='train-select-item'> 发车时间 </div>
      <div className='train-select-item'> 票价 </div>
      <div className='train-select-item'> 一等座 </div>
      <div className='train-select-item'> 二等座 </div>
      <div className='train-select-item'>  </div>
      <div className='train-select-item'>  </div>
    </div>
  );
};

const TrainSelectDetail = (props) => {
  return (
    <div className='train-select-detail'>
      <div className='train-select-item'> {props.trainName} </div>
      <div className='train-select-item'> {props.startTime} </div>
      <div className='train-select-item'> {props.mileage} </div>
      <div className='train-select-item'> {props.firstClassSeatNum} </div>
      <div className='train-select-item'> {props.secondClassSeatNum} </div>
      <Button text={'一等座购票'}/>
      <Button text={'二等座购票'}/>
      {/*<div className='train-select-buy'> 一等座购票 </div>*/}
      {/*<div className='train-select-buy'> 二等座购票 </div>*/}
    </div>
  );
};

const TrainSelect = () => {
  let train = [
    {
      trainId: '1',
      trainName: 'G1',
      trainType: '高铁',
      startTime: '12:00',
      mileage: '1000',
      trainStop: [],
      trainSeats: [
        {seatId: '', carriage: '', seatType: '一等座', seatLocation: ''},
        {seatId: '', carriage: '', seatType: '二等座', seatLocation: ''}
        ]
    }
  ];

  return (
    <div className='train-select'>
      <div className='train-select-title'> 查询结果： </div>
      <TrainSelectTitle/>
      <TrainSelectDetail trainName={train[0].trainName} startTime={train[0].startTime} mileage={train[0].mileage}
                         firstClassSeatNum={1} secondClassSeatNum={2}/>
      <TrainSelectDetail trainName={train[0].trainName} startTime={train[0].startTime} mileage={train[0].mileage}
                         firstClassSeatNum={3} secondClassSeatNum={7}/>
    </div>
  );
};

export default TrainSelect;
