import React from 'react';
import "./Login.css"

const Login = () => {
  let user = {
    username: "",
    password: ""
  };

  const toPostPara = (obj) => {
    let output = "";
    for (const item in obj) {
      output = output + item + "=" + obj[item] + "&"
    }
    return output.substring(0, output.length - 1);
  };

  const loginInformationChange = (e) => {
    const type = e.target.getAttribute("data-id");
    user[type] = e.target.value;
  };

  const login = () => {
    console.log("user login");
    fetch('http://localhost:8080/TrainTickets/updateUser',{
      method:'post',
      mode: 'cors',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      },
      body: toPostPara(user)
    })
      .then(res => {
        const json = res.json();
        if (json.statusCode === 1000) {
          window.location.href = "#/train";
        } else if (json.statusCode === 1001) {
          alert("您被限制购票");
        } else {
          alert("您输入的账号密码不正确")
        }
      })
  };

  const goRegister = () => {
    window.location.href = "#/register"
  };

  return (
    <div className="login">
      <div className="login-title"> 数据库课设-12307火车售票-登录 </div>
      <input className="login-input" placeholder="用户名" data-id="username" onChange={loginInformationChange}/>
      <input className="login-input" placeholder="密码" data-id="password" onChange={loginInformationChange}/>
      <div className="login-button" onClick={login}> 登录 </div>
      <div className="go-register-button" onClick={goRegister}> 点击此处注册新用户 </div>
    </div>
  );
};

export default Login;
