import React from 'react';
import './UserDetail.css';
import Button from '../../../components/button/Button';
import Tools from '../../../tools/Tools';

function UserDetail() {
  let user = window.user;

  function userInformationChange(e) {
    const type = e.target.getAttribute('data-id');
    user[type] = e.target.value;
  }

  function updateUser() {
    console.log('update user');
    console.log(user);
    fetch('http://localhost:8080/TrainTickets/updateUser',{
      method:'post',
      mode: 'cors',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      },
      body: Tools.toPostPara(user)
    })
      .then(res=>res.json())
      .then(json=>{console.log(json)})
  }

  return (
    <div className='user'>
      <input className='user-input' placeholder='用户名' data-id='username' value={user.userName} onChange={userInformationChange}/>
      <input className='user-input' placeholder='密码' data-id='password' value={user.password} onChange={userInformationChange}/>
      <input className='user-input' placeholder='电话号码' data-id='telephone' value={user.telephone} onChange={userInformationChange}/>
      <input className='user-input' placeholder='身份证号（不应该修改）' data-id='idcard' value={user.idcard} onChange={userInformationChange}/>
      <div className='status-select'>
        <div className='status-select-text'> 选择用户状态： </div>
        <select className='status-select-content' data-id='status' defaultValue='正常' onChange={userInformationChange}>
          <option value ='0'> 正常 </option>
          <option value ='1'> 限制购票 </option>
        </select>
      </div>
      <Button class={'update-user'} text={'修改'} onClick={updateUser}/>
      <div className='tips'> 这里的修改仅作为功能演示，实际上在正式的系统中，不应该对身份证号和用户状态进行更改 </div>
    </div>
  );
}

export default UserDetail;
