import React from 'react';
import './TrainSelect.css'
import Button from '../../../components/button/Button';
import Tools from "../../../tools/Tools";

const TrainSelectTitle = () => {
  return (
    <div className='train-select-detail'>
      <div className='train-select-item'> 火车号</div>
      <div className='train-select-item'> 发车时间 </div>
      <div className='train-select-item'> 票价 </div>
      <div className='train-select-item'> 一等座 </div>
      <div className='train-select-item'> 二等座 </div>
      <div className='train-select-item'>  </div>
      <div className='train-select-item'>  </div>
    </div>
  );
};

const TrainSelectDetail = (props) => {
  const queryRemainSeats = () => {
    const para = {
      trainId: props.train.trainId,
      date: props.train.date,
      fromStopId: props.train.startIndex,
      toStopId: props.train.endIndex
    };

    fetch('http://localhost:8080/TrainTickets/getRemainSeats', {
      method: 'post',
      mode: 'cors',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      },
      body: Tools.toPostPara(para)
    })
      .then(res => res.json())
      .then(json => {
        if (json.statusCode === '1000') {
          const seats = JSON.parse(json.content)
          for (const item of seats) {
            if (item.type === '一等座') {
              props.firstClassSeatNum++;
            } else if (item.type === '二等座') {
              props.secondClassSeatNum++;
            }
          }
          console.log(JSON.parse(json.content));
        } else {
          alert('出错啦');
        }
      });
  };

  return (
    <div className='train-select-detail' onClick={queryRemainSeats}>
      <div className='train-select-item'> {props.train.trainName} </div>
      <div className='train-select-item'> {props.train.startTime} </div>
      <div className='train-select-item'> {props.train.mileage} </div>
      <div className='train-select-item'> {props.firstClassSeatNum} </div>
      <div className='train-select-item'> {props.secondClassSeatNum} </div>
      <Button class={'train-tickets-button-default'} text={'一等座购票'}/>
      <Button class={'train-tickets-button-default'} text={'二等座购票'}/>
    </div>
  );
};

const TrainSelect = (props) => {
  // 遍历数组添加组件
  const listItems = props.trainList.map((item, index) =>
    <TrainSelectDetail key={index} train={item} firstClassSeatNum={'点击查询余座数'} secondClassSeatNum={'点击查询余座数'}/>
  );

  return (
    <div className='train-select'>
      <div className='train-select-title'> 查询结果： </div>
      <TrainSelectTitle/>
      <div> {listItems} </div>
    </div>
  );
};

export default TrainSelect;
