import React from 'react';
import './OrderDetail.css';
import Button from "../../../components/button/Button";
import Tools from "../../../tools/Tools";

const OrderSelectTitle = () => {
  return (
    <div className='order-select-detail'>
      <div className='order-select-item'> 火车号 </div>
      <div className='order-select-item'> 发车日 </div>
      <div className='order-select-item'> 发车时间 </div>
      <div className='order-select-item'> 车厢 </div>
      <div className='order-select-item'> 座位号 </div>
      <div className='order-select-item'>  </div>
      <div className='order-select-item'>  </div>
    </div>
  );
};

const OrderSelectDetail = (props) => {
  return (
    <div className='order-select-detail'>
      <div className='order-select-item'> {props.trainName} </div>
      <div className='order-select-item'> {props.date} </div>
      <div className='order-select-item'> {props.startTime} </div>
      <div className='order-select-item'> {props.carriage} </div>
      <div className='order-select-item'> {props.location} </div>
      <Button class={'train-tickets-button-default'} text={'改签'}/>
      <Button class={'train-tickets-button-default'} text={'退票'}/>
    </div>
  );
};

class OrderDetail extends React.Component {
  constructor(props) {
    super(props);
    this.orderList = [];
    this.queryRemainSeats = this.queryRemainSeats.bind(this);
    this.state = {
      first: '',
      second: ''
    };
  }

  // 组件刚挂载时需要先获取到余票情况
  componentDidMount() {
    const para = {
      idcard: '1',
    };

    fetch('http://localhost:8080/TrainTickets/queryOrder', {
      method: 'post',
      mode: 'cors',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      },
      body: Tools.toPostPara(para)
    })
      .then(res => res.json())
      .then(json => {
        if (json.statusCode === '1000') {
          this.orderList = JSON.parse(json.content);
          console.log(JSON.parse(json.content));
        } else {
          alert('出错啦');
        }
      });
  }

  render () {
    const listItems = this.orderList.map((number) =>
      <li>{number}</li>
    );

    return (
      <div className='order-detail'>
        <OrderSelectTitle/>
        <OrderSelectDetail trainName={order[0].trainName} date={order[0].date} startTime={order[0].startTime}
                           carriage={order[0].carriage} location={order[0].location}/>
        <OrderSelectDetail trainName={order[1].trainName} date={order[1].date} startTime={order[1].startTime}
                           carriage={order[1].carriage} location={order[1].location}/>
      </div>
    );
  }
}

export default OrderDetail;
