import React from 'react';
import './Style.css';

function User() {
  let user = {
    username: "",
    password: "",
    telephone: "",
    idcard: "",
    status: ""
  };

  function userInformationChange() {
    this.setState({
      InputValue: event.target.value,

    });
  }

  function updateUser() {
    console.log("update user");

  }

  return (
    <div className="user">
      <div className="tips"> 这里的修改仅作为功能演示，实际上在正式的系统中，不应该对身份证号和用户状态进行更改 </div>
      <input className="user-input" placeholder="用户名" data-id="username"/>
      <input className="user-input" placeholder="密码" data-id="password"/>
      <input className="user-input" placeholder="电话号码" data-id="telephone"/>
      <input className="user-input" placeholder="身份证号（不应该修改）" data-id="idcard"/>
      <div className="status-select">
        <div className="status-select-text"> 选择用户状态： </div>
        <select className="status-select-content" data-id="status">
          <option value ="正常"> 正常 </option>
          <option value ="限制购票"> 限制购票 </option>
        </select>
      </div>
      <div className="update-user" onClick={updateUser}> 修改 </div>
    </div>
  );
}

export default User;
