import React from 'react';
import './CitySelect.css';
import Button from '../../../components/button/Button';
import Tools from "../../../tools/Tools";

class CitySelect extends React.Component {
  city = ['沈阳', '济南', '深圳', '北京', '上海', '广州', '长春'];
  date = ['2019-01-01', '2019-01-02', '2019-01-03'];

  constructor (props) {
    super(props);
    this.fromChange = this.fromChange.bind(this);
    this.toChange = this.toChange.bind(this);
    this.state = {
      fromSelect: 0,
      toSelect: 0,
      dateSelect: 0
    };
  }

  fromChange = (e) => {
    this.setState({isLoggedIn: true});
    fromSelect = e.target.value;
  };

  toChange = (e) => {
    toSelect = e.target.value;
  };

  dateChange = (e) => {
    dateSelect = e.target.value;
  };

  queryTrain = (e) => {
    const para = {
      fromCity: city[fromSelect],
      toCity: city[toSelect],
      date: date[dateSelect]
    };

    fetch('http://localhost:8080/TrainTickets/getTrainByStop',{
      method:'post',
      mode: 'cors',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      },
      body: Tools.toPostPara(para)
    })
      .then(res=>res.json())
      .then(json => {
        if (json.statusCode === '1000') {
          console.log(json);
        } else {
          alert('出错啦');
        }
      })
  };

  render() {

    return (
      <div className='city-select'>
        <select className='city-selector' defaultValue='0' onChange={fromChange}>
          <option value ='0'> {city[0]} </option>
          <option value ='1'> {city[1]} </option>
          <option value ='2'> {city[2]} </option>
          <option value ='3'> {city[3]} </option>
          <option value ='4'> {city[4]} </option>
          <option value ='5'> {city[5]} </option>
          <option value ='6'> {city[6]} </option>
        </select>
        <div className='city-selector-content'> 至 </div>
        <select className='city-selector' defaultValue='0' onChange={toChange}>
          <option value ='0'> {city[0]} </option>
          <option value ='1'> {city[1]} </option>
          <option value ='2'> {city[2]} </option>
          <option value ='3'> {city[3]} </option>
          <option value ='4'> {city[4]} </option>
          <option value ='5'> {city[5]} </option>
          <option value ='6'> {city[6]} </option>
        </select>
        <select className='city-selector' defaultValue='0' onChange={dateChange}>
          <option value ='0'> {date[0]} </option>
          <option value ='1'> {date[1]} </option>
          <option value ='2'> {date[2]} </option>
        </select>
        <Button class={'train-tickets-button-default'} text={'查询'} onClick={queryTrain}/>
      </div>
    );
  }
}

export default CitySelect;
